import React, { useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const Login = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleLogin = () => {
    axios
      .post('http://127.0.0.1:5000/login', {
        name,
        email,
        password,
      })
      .then((response) => {
        const token = response.data.token;
        console.log('Login successful', token);

        // Store the token in localStorage
        localStorage.setItem('token', token);
      })
      .catch((error) => {
        console.error('Login error:', error);
        setError('Login failed. Please check your credentials.');
      });
  };

  return (
    <div className="bg-black shadow-md rounded px-8 pt-6 pb-8 mb-4 w-1/3 mx-auto mt-10">
    <h2 className="text-2xl font-semibold mb-4 text-center">Login</h2>
        <div className="mb-4">
          <input
            type="text"
            placeholder="Name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            className="w-full p-2 border border-gray-300 rounded"
          />
        </div>
        <div className="mb-4">
          <input
            type="text"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            className="w-full p-2 border border-gray-300 rounded"
          />
        </div>
        <div className="mb-4">
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="w-full p-2 border border-gray-300 rounded"
          />
        </div>
        {error && <p className="text-red-500 mb-4">{error}</p>}
        {/* Use Link to navigate to the Swift page */}
        <Link to="/swift">
          <button
            onClick={handleLogin}
            className="w-full flex items-center justify-center bg-blue-500 text-white p-2 rounded hover:bg-blue-600"
          >
            Login
          </button>
        </Link>
        <p className="mt-4">
          Don't have an account?{' '}
          <Link to="/signup" className="text-green-500 hover:underline">
            Signup
          </Link>
        </p>
      </div>
  );
};

export default Login;
