// User.js

// Function to fetch and display users
async function fetchUsers() {
  try {
    // Make a GET request to your Flask API endpoint
    const response = await fetch('http://localhost:5000/user', {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
        'x-access-token': 'YOUR_ACCESS_TOKEN_HERE' // Replace with a valid access token
      }
    });

    if (!response.ok) {
      throw new Error(`Error: ${response.status} - ${response.statusText}`);
    }

    const data = await response.json();

    // Check if the response contains a 'users' key
    if (data.users) {
      const userList = data.users;

      // Create an HTML element to display the users
      const userListElement = document.getElementById('user-list');

      // Clear any existing content
      userListElement.innerHTML = '';

      // Loop through the list of users and create list items
      userList.forEach(user => {
        const listItem = document.createElement('li');
        listItem.textContent = `Name: ${user.name}, Email: ${user.email}`;
        userListElement.appendChild(listItem);
      });
    } else {
      console.error('No users found.');
    }
  } catch (error) {
    console.error('Error:', error.message);
  }
}

// Call the fetchUsers function when the page loads
window.onload = fetchUsers;
